# -*- coding: utf-8 -*-
# https://leetcode-cn.com/problems/count-binary-substrings/


class Solution:
    def count_in_sequence(self, string, length, start):
        zeros, ones = 0, 0
        prev, revert_count = None, 0
        for i in range(start, length):
            if prev and string[i] != prev:
                revert_count = revert_count + 1
            prev = string[i]
            if revert_count >= 2:
                break

            if string[i] == '0':
                zeros = zeros + 1
            else:
                ones = ones + 1
        return zeros, ones

    def countBinarySubstrings(self, string):
        length = len(string)
        index, result = 0, 0
        while index < length:
            zeros, ones = self.count_in_sequence(string, length, index)
            print(index, zeros, ones)
            if string[index] == '0':
                if ones >= zeros:
                    result = result + zeros
                    index = index + zeros
                else:
                    index = index + (zeros - ones)
            elif string[index] == '1':
                if ones <= zeros:
                    result = result + ones
                    index = index + ones
                else:
                    index = index + (ones - zeros)

        return result


if __name__ == '__main__':
    s = Solution()
    r = s.countBinarySubstrings("00110011")
    print(r)
    assert r == 6

    r = s.countBinarySubstrings("111100")
    print(r)
    assert r == 2

    r = s.countBinarySubstrings("11110000")
    print(r)
    assert r == 4

    text = ""

    r = s.countBinarySubstrings(text)
    print(r)
    assert r == 26360
